func:
// See: https://www.home-assistant.io/docs/mqtt/discovery/

// PREPARE DISCOVERY TOPIC
//
discovery_prefix = "homeassistant";

// <component>: One of the supported MQTT components, eg. sensor, binary_sensor
component = "sensor";

// <node_id>: ID of the node providing the topic, this is not used by Home Assistant 
// but may be used to structure the MQTT topic. The ID of the node must only consist
// of characters from the character class [a-zA-Z0-9_-] (alphanumerics, underscore
// and hyphen).
node_id = msg.device["type"];

// <object_id>: The ID of the device. This is only to allow for separate topics for
// each device and is not used for the entity_id. The ID of the device must only 
// consist of characters from the character class [a-zA-Z0-9_-] (alphanumerics, 
// underscore and hyphen).


// DEFAULT SETTINGS
//
msg.ha_mqtt = {
    // DEFAULTS
    "topic_prefix": discovery_prefix + "/" + component + "/" + node_id,

    // INPUT VALUES
    "selectValue": msg.selectValue,
    "measurement": msg.measurement,
    "unit": msg.unit,
    "component": component,

    "device": {
         "name": msg.device["name"],
         "model": msg.device["model"],
         "manufacturer": msg.device["manufacturer"],
         //"connections": msg.device["connections"],
         "identifiers": msg.device["identifiers"]
        }
};

return msg;

// DEFAULT SETTINGS
msg.device = {
    "type": "heatpump",

    // DEVICE INFORMATION
    "name": "Lüftungsintegralsystem",
    "model": "LWZ504E",
    "manufacturer": "Stiebel Eltron",
    "connections": [['mac', '02:5b:26:a8:dc:12']],
    //device_identifiers = [['serial', '233514 9029128631']];
    "identifiers": "233514 9029128631"
};

return msg;

error_text = "";

// Derive "mqtt_topic_prefix"
if (typeof msg.selectValue === 'undefined' || msg.selectValue == "")
{
    error_text = "msg.selectValue is empty!";
}
// Derive "device_class"
else if (typeof msg.measurement === 'undefined' || msg.measurement == "")
{
    error_text = "msg.measurement is empty!";
}
// Derive "unit_of_measurement"
else if (typeof msg.unit === 'undefined' || msg.unit == "")
{
    error_text = "msg.unit is empty!";
}
// Device information set?
else if ( typeof msg.device === 'undefined' || msg.device.length == 0)
{
    error_text = "msg.device not defined!";
}

if (error_text != "")
{
    node.status({fill:"red",shape:"ring",text:error_text});
    node.warn(error_text);
}

return msg;

function upperCaseFirstLetter(string) {
    return string.charAt(0).toUpperCase() + string.slice(1);
}

function lowerCaseAllWordsExceptFirstLetters(string) {
    return string.replace(/\S*/g, function (word) {
        return word.charAt(0) + word.slice(1).toLowerCase();
    });
}

const umlautMap = {
  '\u00dc': 'UE',
  '\u00c4': 'AE',
  '\u00d6': 'OE',
  '\u00fc': 'ue',
  '\u00e4': 'ae',
  '\u00f6': 'oe',
  '\u00df': 'ss',
}

function replaceUmlaute(str) {
  return str
    .replace(/[\u00dc|\u00c4|\u00d6][a-z]/g, (a) => {
      const big = umlautMap[a.slice(0, 1)];
      return big.charAt(0) + big.charAt(1).toLowerCase() + a.slice(1);
    })
    .replace(new RegExp('['+Object.keys(umlautMap).join('|')+']',"g"),
      (a) => umlautMap[a]
    );
}

// INPUTS
selectValue = msg.ha_mqtt["selectValue"];

msg.ha_mqtt["comp_name"] = upperCaseFirstLetter(lowerCaseAllWordsExceptFirstLetters(selectValue));
ha_comp_name_lc = selectValue.toLowerCase().replace(/\ /g, "_").replace(/\./g, "");
msg.ha_mqtt["comp_name_lc"] = replaceUmlaute(ha_comp_name_lc);

msg.ha_mqtt["topic_prefix"] += "/" + msg.ha_mqtt["comp_name_lc"];

return msg;

// Setup message
msg.topic = msg.ha_mqtt["topic_prefix"] + "/state";

// msg.payload contains already the new value

// binary sensor will receive an instant state update after subscription and HA will display the correct state on startup
msg.retain = true;
return msg;

// Setup message
msg.topic = msg.ha_mqtt["topic_prefix"] + "/config";

// Build discovery payload
// See: https://www.home-assistant.io/docs/mqtt/discovery/
msg.payload = {
   "name": msg.ha_mqtt["comp_name"],
   "device": msg.ha_mqtt["device"],
   "unique_id": msg.ha_mqtt["unique_id"],
   "state_topic": msg.ha_mqtt["topic_prefix"] + "/state"
}
if (msg.ha_mqtt["device_class"] != "none" )
    msg.payload["device_class"] = msg.ha_mqtt["device_class"];
if (msg.ha_mqtt["comp_unit"] != "" )
    msg.payload["unit_of_measurement"] = msg.ha_mqtt["comp_unit"];

// binary sensor will receive an instant state update after subscription and HA will display the correct state on startup
msg.retain = true;
return msg;

// COMPONENT DETAILS
switch (msg.ha_mqtt["measurement"]) {
  case "temperature":
    msg.ha_mqtt["device_class"] = "temperature";
    break;
  case "humidity":
    msg.ha_mqtt["device_class"] = "humidity";
    break;
  case "frequency":
    msg.ha_mqtt["device_class"] = "none";
    break;
  case "energy":
    msg.ha_mqtt["device_class"] = "energy";
    break;
  case "power":
    msg.ha_mqtt["device_class"] = "power";
    break;
  case "volume_flow":
  case "state":
    msg.ha_mqtt["device_class"] = "none";
    break;
  default:
    error_text = "Unknown measurement: " + msg.ha_mqtt["measurement"];
    node.status({fill:"red",shape:"ring",text:error_text});
    node.warn(error_text);
    break;
}


// '%'' - humidity, '°C' - degree celcius
switch (msg.ha_mqtt["unit"]) {
  case "celsius": // temperature
    msg.ha_mqtt["comp_unit"] = "°C";
    break;
  case "percent": // humidity
    msg.ha_mqtt["comp_unit"] = "%"
    break;
  case "hertz": // frequency
    msg.ha_mqtt["comp_unit"] = "Hz"
    break;
  case "kWh": // energy
    msg.ha_mqtt["comp_unit"] = "kWh"
    break;
  case "W": // power
    msg.ha_mqtt["comp_unit"] = "W"
    break;
  case "litre_per_min": // volume_flow
    msg.ha_mqtt["comp_unit"] = "l/min"
    break;
  case "value": // state
    msg.ha_mqtt["comp_unit"] = ""
    break;
  default:
    error_text = "Unknown unit: " + msg.ha_mqtt["unit"];
    node.status({fill:"red",shape:"ring",text:error_text});
    node.warn(error_text);
    break;
}
msg.ha_mqtt["unique_id"] = msg.ha_mqtt["component"] + "_" + msg.ha_mqtt["comp_name_lc"] + "_" + msg.ha_mqtt["device_class"];

return msg;
